import axios from 'axios'

const API_BASE_URL =
  process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000'

class SOSService {
  constructor() {
    this.baseURL = `${API_BASE_URL}/api/sos`
  }

  async sendSOSAlert(alertData) {
    try {
      const response = await axios.post(`${this.baseURL}/alert`, alertData)
      return response.data
    } catch (error) {
      console.error('Error sending SOS alert:', error)
      throw error
    }
  }

  async getSOSHistory(userId) {
    try {
      const response = await axios.get(`${this.baseURL}/history/${userId}`)
      return response.data
    } catch (error) {
      console.error('Error fetching SOS history:', error)
      throw error
    }
  }

  async cancelSOSAlert(alertId) {
    try {
      const response = await axios.put(`${this.baseURL}/cancel/${alertId}`)
      return response.data
    } catch (error) {
      console.error('Error cancelling SOS alert:', error)
      throw error
    }
  }

  async updateSOSStatus(alertId, status) {
    try {
      const response = await axios.put(`${this.baseURL}/status/${alertId}`, {
        status,
      })
      return response.data
    } catch (error) {
      console.error('Error updating SOS status:', error)
      throw error
    }
  }
}

export const sosService = new SOSService()
export default sosService
